import React, { useState, useEffect, useCallback } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Tag } from 'primereact/tag';
import { Dialog } from 'primereact/dialog';
import { Dropdown } from 'primereact/dropdown';
import { Message } from 'primereact/message';
import { Card } from 'primereact/card';
import { invoiceAPI } from '../services/api';
import { Invoice } from '../types';
import './InvoiceList.css';

interface InvoiceListProps {
  isAdmin: boolean;
}

const InvoiceList: React.FC<InvoiceListProps> = ({ isAdmin }) => {
  const [invoices, setInvoices] = useState<Invoice[]>([]);
  const [loading, setLoading] = useState(true);
  const [showPaymentDialog, setShowPaymentDialog] = useState(false);
  const [selectedInvoice, setSelectedInvoice] = useState<Invoice | null>(null);
  const [paymentMethod, setPaymentMethod] = useState('');
  const [error, setError] = useState('');

  const paymentMethods = [
    { label: 'Payoneer', value: 'Payoneer' },
    { label: 'Western Union', value: 'Western Union' }
  ];

  const fetchInvoices = useCallback(async () => {
    try {
      setLoading(true);
      const response = isAdmin ? await invoiceAPI.getAllInvoices() : await invoiceAPI.getMyInvoices();
      setInvoices(response.data);
    } catch (error: any) {
      setError(error.response?.data?.message || 'Greška pri učitavanju faktura');
    } finally {
      setLoading(false);
    }
  }, [isAdmin]);

  useEffect(() => {
    fetchInvoices();
  }, [fetchInvoices]);

  const handlePayInvoice = (invoice: Invoice) => {
    setSelectedInvoice(invoice);
    setPaymentMethod('');
    setShowPaymentDialog(true);
  };

  const confirmPayment = async () => {
    if (!selectedInvoice || !paymentMethod) return;

    try {
      await invoiceAPI.markAsPaid(selectedInvoice._id, paymentMethod);
      setShowPaymentDialog(false);
      fetchInvoices();
    } catch (error: any) {
      setError(error.response?.data?.message || 'Greška pri označavanju plaćanja');
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('sr-RS', {
      style: 'currency',
      currency: 'RSD'
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('sr-RS');
  };

  const statusBodyTemplate = (rowData: Invoice) => {
    return (
      <Tag
        value={rowData.isPaid ? 'Plaćeno' : 'Neplaćeno'}
        severity={rowData.isPaid ? 'success' : 'danger'}
        className="status-tag"
      />
    );
  };

  const paymentMethodBodyTemplate = (rowData: Invoice) => {
    if (!rowData.paymentMethod) return <span className="text-500">-</span>;
    return (
      <div className="payment-method">
        <i className={`pi ${rowData.paymentMethod === 'Payoneer' ? 'pi-credit-card' : 'pi-send'}`}></i>
        <span>{rowData.paymentMethod}</span>
      </div>
    );
  };

  const actionBodyTemplate = (rowData: Invoice) => {
    if (rowData.isPaid) {
      return (
        <div className="paid-status">
          <i className="pi pi-check-circle text-green-500"></i>
          <span className="text-green-500 font-semibold">Plaćeno</span>
        </div>
      );
    }

    return (
      <Button
        label="Označi kao plaćeno"
        icon="pi pi-check"
        size="small"
        className="pay-button"
        onClick={() => handlePayInvoice(rowData)}
      />
    );
  };

  const amountBodyTemplate = (rowData: Invoice) => {
    return (
      <div className="amount-cell">
        <span className="amount-value">{formatCurrency(rowData.amount)}</span>
      </div>
    );
  };

  const dueDateBodyTemplate = (rowData: Invoice) => {
    const dueDate = new Date(rowData.dueDate);
    const today = new Date();
    const isOverdue = dueDate < today && !rowData.isPaid;

    return (
      <div className={`due-date ${isOverdue ? 'overdue' : ''}`}>
        <i className="pi pi-calendar"></i>
        <span>{formatDate(rowData.dueDate)}</span>
        {isOverdue && <i className="pi pi-exclamation-triangle text-red-500 ml-2"></i>}
      </div>
    );
  };

  return (
    <div className="invoice-list-container">
      <div className="list-header">
        <div className="header-info">
          <h2 className="list-title">
            <i className="pi pi-file-o"></i>
            {isAdmin ? 'Sve fakture' : 'Moje fakture'}
          </h2>
          <p className="list-subtitle">
            {invoices.length} faktura ukupno
          </p>
        </div>
        <Button
          label="Osveži"
          icon="pi pi-refresh"
          className="refresh-button"
          onClick={fetchInvoices}
        />
      </div>

      {error && (
        <Message severity="error" text={error} className="error-message" />
      )}

      <Card className="invoices-card">
        <DataTable
          value={invoices}
          loading={loading}
          paginator
          rows={10}
          rowsPerPageOptions={[5, 10, 25]}
          emptyMessage="Nema faktura"
          className="invoices-table"
          paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
          currentPageReportTemplate="Prikazano {first} do {last} od {totalRecords} faktura"
        >
          <Column field="clientName" header="Klijent" className="client-column" />
          <Column field="clientEmail" header="Email" className="email-column" />
          <Column field="amount" header="Iznos" body={amountBodyTemplate} className="amount-column" />
          <Column field="description" header="Opis" className="description-column" />
          <Column field="dueDate" header="Datum dospijeća" body={dueDateBodyTemplate} className="due-date-column" />
          <Column field="isPaid" header="Status" body={statusBodyTemplate} className="status-column" />
          <Column field="paymentMethod" header="Način plaćanja" body={paymentMethodBodyTemplate} className="payment-column" />
          <Column header="Akcije" body={actionBodyTemplate} className="action-column" />
        </DataTable>
      </Card>

      <Dialog
        header="Označi kao plaćeno"
        visible={showPaymentDialog}
        style={{ width: '25rem' }}
        onHide={() => setShowPaymentDialog(false)}
        className="payment-dialog"
      >
        <div className="payment-form">
          <div className="field">
            <label htmlFor="paymentMethod" className="field-label">Način plaćanja</label>
            <Dropdown
              id="paymentMethod"
              value={paymentMethod}
              options={paymentMethods}
              onChange={(e) => setPaymentMethod(e.value)}
              className="field-input"
              placeholder="Izaberite način plaćanja"
            />
          </div>
          <div className="dialog-actions">
            <Button
              label="Otkaži"
              severity="secondary"
              onClick={() => setShowPaymentDialog(false)}
            />
            <Button
              label="Potvrdi"
              onClick={confirmPayment}
              disabled={!paymentMethod}
            />
          </div>
        </div>
      </Dialog>
    </div>
  );
};

export default InvoiceList;
