import React, { useEffect, useState } from 'react';
import { Card } from 'primereact/card';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Message } from 'primereact/message';
import { Dialog } from 'primereact/dialog';
import { Password } from 'primereact/password';
import { profileAPI } from '../services/api';
import './Profile.css';

// Define a strong type for the profile document we edit
type UserProfile = {
  userId?: string;
  companyName: string;
  fullName: string;
  addressLine: string;
  city: string;
  state: string;
  postalCode: string;
  country: string;
  phone: string;
  logoUrl?: string;
  signatureUrl?: string;
  swiftCode?: string;
  iban?: string;
  cardNumber?: string;
  createdAt?: string;
  updatedAt?: string;
  _id?: string;
};

const defaultProfile: UserProfile = {
  companyName: '',
  fullName: '',
  addressLine: '',
  city: '',
  state: '',
  postalCode: '',
  country: '',
  phone: '',
  logoUrl: '',
  signatureUrl: '',
  swiftCode: '',
  iban: '',
  cardNumber: ''
};

const Profile: React.FC = () => {
  const [form, setForm] = useState<UserProfile>(defaultProfile);
  const [initialLoading, setInitialLoading] = useState(true);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [uploadingLogo, setUploadingLogo] = useState(false);
  const [uploadingSignature, setUploadingSignature] = useState(false);
  const [passwordDialog, setPasswordDialog] = useState(false);
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [changingPassword, setChangingPassword] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    (async () => {
      try {
        const res = await profileAPI.get();
        setForm({ ...defaultProfile, ...(res.data || {}) });
      } catch (e: any) {
        setError(e?.response?.data?.message || 'Failed to load profile');
      } finally {
        setInitialLoading(false);
      }
    })();
  }, []);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  const handleChange = (key: keyof UserProfile, value: string) => {
    setError('');
    setSuccess('');
    setForm((f) => ({ ...f, [key]: value } as UserProfile));
  };

  const buildUpdatePayload = (src: UserProfile) => {
    // Only send editable fields; backend will upsert and set updatedAt
    return {
      companyName: src.companyName || '',
      fullName: src.fullName || '',
      addressLine: src.addressLine || '',
      city: src.city || '',
      state: src.state || '',
      postalCode: src.postalCode || '',
      country: src.country || '',
      phone: src.phone || '',
      logoUrl: src.logoUrl || '',
      signatureUrl: src.signatureUrl || '',
      swiftCode: src.swiftCode || '',
      iban: src.iban || '',
      cardNumber: src.cardNumber || ''
    };
  };

  const saveProfile = async () => {
    setLoading(true); setError(''); setSuccess('');
    try {
      await profileAPI.update(buildUpdatePayload(form));
      setSuccess('Profile updated successfully');
    } catch (e: any) {
      setError(e.response?.data?.message || 'Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  const changePassword = async () => {
    setPasswordError('');
    if (!currentPassword || !newPassword || !confirmPassword) {
      setPasswordError('All fields are required');
      return;
    }
    if (newPassword !== confirmPassword) {
      setPasswordError('New passwords do not match');
      return;
    }
    if (newPassword.length < 6) {
      setPasswordError('New password must be at least 6 characters');
      return;
    }

    setChangingPassword(true);
    try {
      const response = await fetch('/api/profile/change-password', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ currentPassword, newPassword })
      });
      const data = await response.json();
      if (response.ok) {
        setSuccess('Password changed successfully');
        setPasswordDialog(false);
        setCurrentPassword('');
        setNewPassword('');
        setConfirmPassword('');
      } else {
        setPasswordError(data.message || 'Failed to change password');
      }
    } catch (e: any) {
      setPasswordError('Failed to change password');
    } finally {
      setChangingPassword(false);
    }
  };

  const uploadFile = async (file: File, type: 'logo' | 'signature') => {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('type', type);

    try {
      const response = await fetch('/api/upload', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const data = await response.json();
      return data.url;
    } catch (error) {
      throw error;
    }
  };

  const handleFileUpload = async (file: File, type: 'logo' | 'signature') => {
    const setUploading = type === 'logo' ? setUploadingLogo : setUploadingSignature;
    setUploading(true);
    setError('');
    setSuccess('');

    try {
      const url = await uploadFile(file, type);
      const key = type === 'logo' ? 'logoUrl' : 'signatureUrl';
      handleChange(key, url);
      setSuccess(`${type === 'logo' ? 'Logo' : 'Signature'} uploaded successfully`);
    } catch (e: any) {
      setError(`Failed to upload ${type}: ${e.message}`);
    } finally {
      setUploading(false);
    }
  };

  if (initialLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <Card title="Profile Settings">
        {error && <Message severity="error" text={error} style={{ marginBottom: '20px' }} />}
        {success && <Message severity="success" text={success} style={{ marginBottom: '20px' }} />}

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '20px' }}>
          <div>
            <label>Company Name</label>
            <InputText
              value={form.companyName}
              onChange={(e) => handleChange('companyName', e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
          <div>
            <label>Full Name</label>
            <InputText
              value={form.fullName}
              onChange={(e) => handleChange('fullName', e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label>Address Line</label>
          <InputText
            value={form.addressLine}
            onChange={(e) => handleChange('addressLine', e.target.value)}
            style={{ width: '100%' }}
          />
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '20px', marginBottom: '20px' }}>
          <div>
            <label>City</label>
            <InputText
              value={form.city}
              onChange={(e) => handleChange('city', e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
          <div>
            <label>State</label>
            <InputText
              value={form.state}
              onChange={(e) => handleChange('state', e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
          <div>
            <label>Postal Code</label>
            <InputText
              value={form.postalCode}
              onChange={(e) => handleChange('postalCode', e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '20px' }}>
          <div>
            <label>Country</label>
            <InputText
              value={form.country}
              onChange={(e) => handleChange('country', e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
          <div>
            <label>Phone</label>
            <InputText
              value={form.phone}
              onChange={(e) => handleChange('phone', e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '20px' }}>
          <div>
            <label>Logo URL</label>
            <InputText
              value={form.logoUrl}
              onChange={(e) => handleChange('logoUrl', e.target.value)}
              style={{ width: '100%' }}
            />
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                const file = e.target.files?.[0];
                if (file) handleFileUpload(file, 'logo');
              }}
              style={{ marginTop: '8px' }}
            />
            {uploadingLogo && <div>Uploading logo...</div>}
          </div>
          <div>
            <label>Signature URL</label>
            <InputText
              value={form.signatureUrl}
              onChange={(e) => handleChange('signatureUrl', e.target.value)}
              style={{ width: '100%' }}
            />
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                const file = e.target.files?.[0];
                if (file) handleFileUpload(file, 'signature');
              }}
              style={{ marginTop: '8px' }}
            />
            {uploadingSignature && <div>Uploading signature...</div>}
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '20px', marginBottom: '20px' }}>
          <div>
            <label>Swift Code</label>
            <InputText
              value={form.swiftCode}
              onChange={(e) => handleChange('swiftCode', e.target.value)}
              style={{ width: '100%' }}
              placeholder="SWIFT code for international transfers"
            />
          </div>
          <div>
            <label>IBAN</label>
            <InputText
              value={form.iban}
              onChange={(e) => handleChange('iban', e.target.value)}
              style={{ width: '100%' }}
              placeholder="International Bank Account Number"
            />
          </div>
          <div>
            <label>Card Number</label>
            <InputText
              value={form.cardNumber}
              onChange={(e) => handleChange('cardNumber', e.target.value)}
              style={{ width: '100%' }}
              placeholder="Card number for payments"
            />
          </div>
        </div>

        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Button 
            label="Change Password" 
            icon="pi pi-key" 
            severity="warning" 
            outlined 
            onClick={() => setPasswordDialog(true)}
          />
          <Button label="Save" loading={loading} onClick={saveProfile} />
        </div>
      </Card>

      <Dialog 
        header="Change Password"
        visible={passwordDialog}
        onHide={() => setPasswordDialog(false)}
        style={{ width: '400px' }}
      >
        <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
          {passwordError && <Message severity="error" text={passwordError} />}

          <div>
            <label>Current Password:</label>
            <Password 
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              placeholder="Enter current password"
              style={{ width: '100%' }}
            />
          </div>

          <div>
            <label>New Password:</label>
            <Password 
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="Enter new password"
              style={{ width: '100%' }}
            />
          </div>

          <div>
            <label>Confirm New Password:</label>
            <Password 
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Confirm new password"
              style={{ width: '100%' }}
            />
          </div>

          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '8px' }}>
            <Button 
              label="Cancel" 
              severity="secondary" 
              onClick={() => setPasswordDialog(false)}
              outlined 
            />
            <Button 
              label="Change Password" 
              icon="pi pi-key"
              onClick={changePassword}
              loading={changingPassword}
            />
          </div>
        </div>
      </Dialog>
    </div>
  );
};

export default Profile;
