import React, { useState } from 'react';
import { Button } from 'primereact/button';
import { Avatar } from 'primereact/avatar';
import 'primeicons/primeicons.css';
import { Badge } from 'primereact/badge';
import { useAuth } from './AuthContext';
import logo from '../Uptimio Logo.png';
import './Sidebar.css';

interface SidebarProps {
  activeTab: number;
  onTabChange: (index: number) => void;
  isCollapsed: boolean;
  onToggleCollapse: () => void;
  isMobileOpen: boolean;
  onMobileClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ 
  activeTab, 
  onTabChange, 
  isCollapsed, 
  onToggleCollapse,
  isMobileOpen,
  onMobileClose
}) => {
  const { user, logout } = useAuth();
  const [lagerExpanded, setLagerExpanded] = useState(true);

  const isAdmin = user?.role === 'admin';
  const isHilti = user?.role === 'hilti';
  const isSefLagera = user?.role === 'sef_lagera';
  const isViljuskarista = user?.role === 'viljuskarista';
  const isWarehouseRole = isHilti || isSefLagera || isViljuskarista;

  // Menu items za admin i obične korisnike
  const standardMenuItems = [
    {
      id: 0,
      label: 'Invoices',
      icon: 'pi pi-file-o',
      adminOnly: false
    },
    {
      id: 1,
      label: 'Create Invoice',
      icon: 'pi pi-plus',
      adminOnly: true
    },
    {
      id: 2,
      label: 'Profiles',
      icon: 'pi pi-users',
      adminOnly: true
    },
    {
      id: 3,
      label: 'Create User',
      icon: 'pi pi-user-plus',
      adminOnly: true
    },
    {
      id: 4,
      label: 'Profile',
      icon: 'pi pi-briefcase',
      adminOnly: false
    }
  ];

  // Sub-items za Hilti pod Lager gotovih proizvoda
  const hiltiSubItems = [
    {
      id: 5,
      label: 'Proizvodnja',
      icon: 'pi pi-cog',
      roles: ['hilti', 'sef_lagera']
    },
    {
      id: 6,
      label: 'Radnici',
      icon: 'pi pi-users',
      roles: ['hilti', 'sef_lagera']
    },
    {
      id: 7,
      label: 'Proizvodi',
      icon: 'pi pi-tag',
      roles: ['hilti', 'sef_lagera']
    },
    {
      id: 8,
      label: 'Izveštaji',
      icon: 'pi pi-file',
      roles: ['hilti', 'sef_lagera']
    }
  ];

  const handleTabChange = (index: number) => {
    onTabChange(index);
    // Close mobile sidebar when item is selected
    if (window.innerWidth <= 768) {
      onMobileClose();
    }
  };

  const toggleLagerExpanded = () => {
    setLagerExpanded(!lagerExpanded);
  };

  return (
    <>
      {/* Mobile Overlay */}
      {isMobileOpen && (
        <div className="sidebar-overlay" onClick={onMobileClose} />
      )}

      <div className={`sidebar ${isCollapsed ? 'collapsed' : ''} ${isMobileOpen ? 'mobile-open' : ''}`}>
        {/* Header */}
        <div className="sidebar-header">
          <div className="logo-section">
            <img src={logo} alt="Uptimio" className="app-logo" />
          </div>
          <div className="header-buttons">
            <Button
              icon={isCollapsed ? 'pi pi-angle-right' : 'pi pi-angle-left'}
              className="collapse-button"
              onClick={onToggleCollapse}
              text
              rounded
            />
            {/* Mobile Close Button */}
            <Button
              icon="pi pi-times"
              className="mobile-close-button"
              onClick={onMobileClose}
              text
              rounded
              aria-label="Close sidebar"
            />
          </div>
        </div>

        {/* Navigation */}
        <nav className="sidebar-nav">
          {/* Standard menu items za admin i obične korisnike */}
          {!isWarehouseRole && standardMenuItems
            .filter(item => !item.adminOnly || isAdmin)
            .map((item) => (
              <Button
                key={item.id}
                label={!isCollapsed ? item.label : ''}
                icon={item.icon}
                className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
                onClick={() => handleTabChange(item.id)}
                text
                data-tooltip={isCollapsed ? item.label : ''}
              />
            ))}

          {/* Lager gotovih proizvoda - parent kategorija */}
          {isWarehouseRole && (
            <>
              <div className="menu-category">
                <Button
                  label={!isCollapsed ? 'Lager gotovih proizvoda' : ''}
                  icon="pi pi-box"
                  className="nav-item category-header"
                  onClick={toggleLagerExpanded}
                  text
                >
                  {!isCollapsed && (
                    <i className={`pi ${lagerExpanded ? 'pi-chevron-down' : 'pi-chevron-right'} category-toggle`} />
                  )}
                </Button>
              </div>

              {/* Hilti sub-items */}
              {lagerExpanded && (isHilti || isSefLagera) && (
                <div className="submenu-section">
                  <div className="submenu-header">
                    {!isCollapsed && <span className="submenu-title">Hilti</span>}
                  </div>
                  {hiltiSubItems
                    .filter(item => item.roles.includes(user?.role || ''))
                    .map((item) => (
                      <Button
                        key={item.id}
                        label={!isCollapsed ? item.label : ''}
                        icon={item.icon}
                        className={`nav-item submenu-item ${activeTab === item.id ? 'active' : ''}`}
                        onClick={() => handleTabChange(item.id)}
                        text
                        data-tooltip={isCollapsed ? item.label : ''}
                      />
                    ))}
                </div>
              )}
            </>
          )}
        </nav>

        {/* User Info */}
        <div className="sidebar-footer">
          <div className="user-info">
            <Avatar 
              icon="pi pi-user" 
              className="user-avatar"
              size="large"
            />
            {!isCollapsed && (
              <div className="user-details">
                <span className="user-name">{user?.name}</span>
                <Badge 
                  value={
                    user?.role === 'admin' ? 'Admin' :
                    user?.role === 'hilti' ? 'Hilti' :
                    user?.role === 'sef_lagera' ? 'Šef Lagera' :
                    user?.role === 'viljuskarista' ? 'Viljuškarista' :
                    'User'
                  } 
                  severity={user?.role === 'admin' ? 'info' : 'success'}
                  className="role-badge"
                />
              </div>
            )}
          </div>
          <Button
            label={!isCollapsed ? 'Sign out' : ''}
            icon="pi pi-sign-out"
            className="logout-button"
            onClick={logout}
            text
            data-tooltip={isCollapsed ? 'Sign out' : ''}
          />
        </div>
      </div>
    </>
  );
};

export default Sidebar;
