import React, { useEffect, useState } from 'react';
import { Card } from 'primereact/card';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Message } from 'primereact/message';
import { profileAPI } from '../services/api';

const Profile: React.FC = () => {
  const [form, setForm] = useState<any>({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [uploadingLogo, setUploadingLogo] = useState(false);
  const [uploadingSignature, setUploadingSignature] = useState(false);

  useEffect(() => {
    (async () => {
      try {
        const res = await profileAPI.get();
        setForm(res.data || {});
      } catch (e: any) {
        setError(e.response?.data?.message || 'Failed to load profile');
      }
    })();
  }, []);

  const set = (key: string, value: any) => setForm((f: any) => ({ ...f, [key]: value }));

  const onSave = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(''); setSuccess(''); setLoading(true);
    try {
      const { data } = await profileAPI.update(form);
      setForm(data);
      setSuccess('Profile saved successfully.');
    } catch (e: any) {
      setError(e.response?.data?.message || 'Failed to save profile');
    } finally {
      setLoading(false);
    }
  };

  const onUploadLogo = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;
    setUploadingLogo(true); setError(''); setSuccess('');
    try {
      const { data } = await profileAPI.uploadLogo(file);
      setForm((f: any) => ({ ...f, logoUrl: data.url }));
      setSuccess('Logo uploaded.');
    } catch (e: any) {
      setError(e.response?.data?.message || 'Failed to upload logo');
    } finally {
      setUploadingLogo(false);
    }
  };

  const onUploadSignature = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;
    setUploadingSignature(true); setError(''); setSuccess('');
    try {
      const { data } = await profileAPI.uploadSignature(file);
      setForm((f: any) => ({ ...f, signatureUrl: data.url }));
      setSuccess('Signature uploaded.');
    } catch (e: any) {
      setError(e.response?.data?.message || 'Failed to upload signature');
    } finally {
      setUploadingSignature(false);
    }
  };

  return (
    <div className="profile-container">
      <Card>
        <h2>Business Profile</h2>
        <p>These details will appear on your invoices (currency: USD).</p>
        <form onSubmit={onSave} className="profile-form">
          <div className="field-grid">
            <div className="field">
              <label>Full name</label>
              <InputText value={form.fullName || ''} onChange={(e) => set('fullName', e.target.value)} />
            </div>
            <div className="field">
              <label>Company name</label>
              <InputText value={form.companyName || ''} onChange={(e) => set('companyName', e.target.value)} />
            </div>
            <div className="field">
              <label>Address</label>
              <InputText value={form.addressLine || ''} onChange={(e) => set('addressLine', e.target.value)} />
            </div>
            <div className="field">
              <label>City</label>
              <InputText value={form.city || ''} onChange={(e) => set('city', e.target.value)} />
            </div>
            <div className="field">
              <label>State/Province</label>
              <InputText value={form.state || ''} onChange={(e) => set('state', e.target.value)} />
            </div>
            <div className="field">
              <label>Postal code</label>
              <InputText value={form.postalCode || ''} onChange={(e) => set('postalCode', e.target.value)} />
            </div>
            <div className="field">
              <label>Country</label>
              <InputText value={form.country || ''} onChange={(e) => set('country', e.target.value)} />
            </div>
            <div className="field">
              <label>Phone</label>
              <InputText value={form.phone || ''} onChange={(e) => set('phone', e.target.value)} />
            </div>

            <div className="field">
              <label>Logo</label>
              <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
                <input type="file" accept="image/*" onChange={onUploadLogo} disabled={uploadingLogo} />
                {form.logoUrl && <img src={form.logoUrl} alt="logo" style={{ height: 40 }} />}
              </div>
            </div>

            <div className="field">
              <label>Signature</label>
              <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
                <input type="file" accept="image/*" onChange={onUploadSignature} disabled={uploadingSignature} />
                {form.signatureUrl && <img src={form.signatureUrl} alt="signature" style={{ height: 40 }} />}
              </div>
            </div>
          </div>

          {error && <Message severity="error" text={error} />}
          {success && <Message severity="success" text={success} />}

          <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: 16 }}>
            <Button type="submit" label="Save profile" loading={loading} />
          </div>
        </form>
      </Card>
    </div>
  );
};

export default Profile;
