import React, { useEffect, useMemo, useState } from 'react';
import { Card } from 'primereact/card';
import { Button } from 'primereact/button';
import { Message } from 'primereact/message';
import { invoiceAPI } from '../services/api';
import { Invoice } from '../types';
import UptimioLogo from '../Uptimio Logo.png';
import SignatureImg from '../Jeremic Nikola Signature.png';

function useQuery() {
  return useMemo(() => new URLSearchParams(window.location.search), []);
}

const a4Styles: React.CSSProperties = {
  width: '210mm',
  minHeight: '297mm',
  margin: '0 auto',
  background: '#fff',
  padding: '16mm',
  boxSizing: 'border-box',
  position: 'relative'
};

const InvoiceView: React.FC = () => {
  const query = useQuery();
  const invoiceId = query.get('invoiceId');
  const [invoice, setInvoice] = useState<Invoice | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    (async () => {
      if (!invoiceId) { setError('Missing invoiceId'); return; }
      setLoading(true);
      try {
        const { data } = await invoiceAPI.getById(invoiceId);
        setInvoice(data);
      } catch (e: any) {
        setError(e.response?.data?.message || 'Failed to load invoice');
      } finally {
        setLoading(false);
      }
    })();
  }, [invoiceId]);

  const total = invoice?.amount || 0;

  return (
    <div className="invoice-view" style={{ padding: 24 }}>
      <style>{`
        @media print {
          body { -webkit-print-color-adjust: exact; print-color-adjust: exact; }
          .no-print { display: none !important; }
          .invoice-paper { box-shadow: none !important; }
        }
        .invoice-table th, .invoice-table td { border-bottom: 1px solid #e5e7eb; }
      `}</style>
      <Card>
        <div className="no-print" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <h2 style={{ margin: 0 }}>Invoice</h2>
          <Button label="Print / Save PDF" icon="pi pi-print" onClick={() => window.print()} />
        </div>
        {loading && <p>Loading...</p>}
        {error && <Message severity="error" text={error} />}
        {invoice && (
          <div className="invoice-paper" style={{ marginTop: 16, ...a4Styles }}>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <div>
                {/* Uptimio logo above issuer name */}
                <img src={UptimioLogo} alt="Uptimio" style={{ height: 36, marginBottom: 6 }} />
                {invoice.issuerLogoUrl && <img src={invoice.issuerLogoUrl} alt="issuer logo" style={{ height: 36, marginBottom: 6, marginLeft: 8 }} />}
                <div style={{ marginTop: 4, fontWeight: 600 }}>{invoice.issuerName}</div>
                {invoice.issuerCompany && <div>{invoice.issuerCompany}</div>}
                {invoice.issuerAddress && <div>{invoice.issuerAddress}</div>}
                {invoice.issuerCountry && <div>{invoice.issuerCountry}</div>}
                {invoice.issuerPhone && <div>{invoice.issuerPhone}</div>}
              </div>
              <div style={{ textAlign: 'right' }}>
                <div style={{ fontWeight: 600 }}>Bill to</div>
                <div>{invoice.clientName}</div>
                <div>{invoice.clientEmail}</div>
                {invoice.clientCompany && <div>{invoice.clientCompany}</div>}
                {invoice.clientAddress && <div>{invoice.clientAddress}</div>}
                {invoice.clientCountry && <div>{invoice.clientCountry}</div>}
                {invoice.clientPhone && <div>{invoice.clientPhone}</div>}
                <div style={{ marginTop: 8 }}>Due date: {new Date(invoice.dueDate).toLocaleDateString('en-US')}</div>
              </div>
            </div>

            <div style={{ marginTop: 16 }}>
              <table className="invoice-table" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr>
                    <th style={{ textAlign: 'left', padding: '8px 0' }}>Description</th>
                    <th style={{ textAlign: 'right', padding: '8px 0' }}>Qty</th>
                    <th style={{ textAlign: 'right', padding: '8px 0' }}>Unit price</th>
                    <th style={{ textAlign: 'right', padding: '8px 0' }}>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {invoice.items?.map((it, idx) => (
                    <tr key={idx}>
                      <td style={{ padding: '6px 0' }}>{it.description}</td>
                      <td style={{ padding: '6px 0', textAlign: 'right' }}>{it.quantity}</td>
                      <td style={{ padding: '6px 0', textAlign: 'right' }}>{(it.unitPrice).toLocaleString('en-US', { style: 'currency', currency: invoice.currency })}</td>
                      <td style={{ padding: '6px 0', textAlign: 'right' }}>{(it.quantity * it.unitPrice).toLocaleString('en-US', { style: 'currency', currency: invoice.currency })}</td>
                    </tr>
                  ))}
                </tbody>
                <tfoot>
                  <tr>
                    <td colSpan={3} style={{ textAlign: 'right', paddingTop: 8, fontWeight: 600 }}>Total</td>
                    <td style={{ textAlign: 'right', paddingTop: 8, fontWeight: 600 }}>{total.toLocaleString('en-US', { style: 'currency', currency: invoice.currency })}</td>
                  </tr>
                </tfoot>
              </table>
            </div>

            {/* Payment method */}
            {(invoice as any).paymentMethod && (
              <div style={{ marginTop: 16 }}>
                <div style={{ fontWeight: 600 }}>Payment method</div>
                <div>{(invoice as any).paymentMethod}</div>
              </div>
            )}

            {invoice.notes && (
              <div style={{ marginTop: 16 }}>
                <div style={{ fontWeight: 600 }}>Notes</div>
                <div>{invoice.notes}</div>
              </div>
            )}

            {/* Hard-coded signature at bottom of A4 */}
            <div style={{ position: 'absolute', left: '16mm', right: '16mm', bottom: '16mm' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
                <img src={SignatureImg} alt="signature" style={{ height: 56 }} />
                <div style={{ fontWeight: 600 }}>{invoice.issuerName}</div>
              </div>
            </div>
          </div>
        )}
      </Card>
    </div>
  );
};

export default InvoiceView;
