import React, { useState } from 'react';
import { TabView, TabPanel } from 'primereact/tabview';
import { Button } from 'primereact/button';
import { Avatar } from 'primereact/avatar';
import { Badge } from 'primereact/badge';
import { useAuth } from './AuthContext';
import InvoiceList from './InvoiceList';
import CreateInvoice from './CreateInvoice';
import './Dashboard.css';

const Dashboard: React.FC = () => {
  const { user, logout } = useAuth();
  const [activeIndex, setActiveIndex] = useState(0);

  const isAdmin = user?.role === 'admin';

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <div className="header-content">
          <div className="header-left">
            <div className="logo-section">
              <i className="pi pi-credit-card text-3xl text-white"></i>
              <div className="logo-text">
                <h1 className="app-title">Uptiomio</h1>
                <p className="app-subtitle">Upravljanje fakturima i plaÄ‡anjima</p>
              </div>
            </div>
          </div>

          <div className="header-right">
            <div className="user-info">
              <Avatar 
                icon="pi pi-user" 
                className="user-avatar"
                size="large"
              />
              <div className="user-details">
                <span className="user-name">{user?.name}</span>
                <Badge 
                  value={user?.role === 'admin' ? 'Administrator' : 'Korisnik'} 
                  severity={user?.role === 'admin' ? 'info' : 'success'}
                  className="role-badge"
                />
              </div>
            </div>
            <Button
              label="Odjavi se"
              icon="pi pi-sign-out"
              severity="secondary"
              className="logout-button"
              onClick={logout}
            />
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="dashboard-content">
        <div className="content-wrapper">
          <TabView 
            activeIndex={activeIndex} 
            onTabChange={(e) => setActiveIndex(e.index)}
            className="main-tabs"
          >
            <TabPanel 
              header={
                <div className="tab-header">
                  <i className="pi pi-file-o"></i>
                  <span>Fakture</span>
                </div>
              }
            >
              <InvoiceList isAdmin={isAdmin} />
            </TabPanel>

            {isAdmin && (
              <TabPanel 
                header={
                  <div className="tab-header">
                    <i className="pi pi-plus"></i>
                    <span>Kreiraj fakturu</span>
                  </div>
                }
              >
                <CreateInvoice />
              </TabPanel>
            )}
          </TabView>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
