import React, { useState } from 'react';
import { TabView, TabPanel } from 'primereact/tabview';
import { Button } from 'primereact/button';
import { useAuth } from './AuthContext';
import InvoiceList from './InvoiceList';
import CreateInvoice from './CreateInvoice';

const Dashboard: React.FC = () => {
  const { user, logout } = useAuth();
  const [activeIndex, setActiveIndex] = useState(0);

  const isAdmin = user?.role === 'admin';

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-2 border-bottom-1 border-300">
        <div className="flex justify-content-between align-items-center p-4">
          <div>
            <h1 className="text-2xl font-bold text-primary m-0">Uptiomio</h1>
            <p className="text-600 m-0">Upravljanje fakturima i plaćanjima</p>
          </div>
          <div className="flex align-items-center gap-3">
            <span className="text-600">
              Dobrodošli, {user?.name} ({user?.role === 'admin' ? 'Administrator' : 'Korisnik'})
            </span>
            <Button
              label="Odjavi se"
              icon="pi pi-sign-out"
              severity="secondary"
              onClick={logout}
            />
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="p-4">
        <TabView activeIndex={activeIndex} onTabChange={(e) => setActiveIndex(e.index)}>
          <TabPanel header="Fakture">
            <InvoiceList isAdmin={isAdmin} />
          </TabPanel>

          {isAdmin && (
            <TabPanel header="Kreiraj fakturu">
              <CreateInvoice />
            </TabPanel>
          )}
        </TabView>
      </div>
    </div>
  );
};

export default Dashboard;
