import React, { useState } from 'react';
import { AuthProvider, useAuth } from './components/AuthContext';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import { Button } from 'primereact/button';
import { Card } from 'primereact/card';
import 'primereact/resources/themes/lara-light-indigo/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';

const AppContent: React.FC = () => {
  const { user, loading } = useAuth();
  const [showRegister, setShowRegister] = useState(false);

  if (loading) {
    return (
      <div className="flex justify-content-center align-items-center min-h-screen">
        <div className="text-center">
          <i className="pi pi-spin pi-spinner text-4xl text-primary"></i>
          <p className="mt-3 text-600">Učitavanje...</p>
        </div>
      </div>
    );
  }

  if (user) {
    return <Dashboard />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="flex justify-content-center align-items-center min-h-screen">
        <div className="w-full max-w-md">
          <Card className="text-center mb-4">
            <h1 className="text-3xl font-bold text-primary mb-2">Uptiomio</h1>
            <p className="text-600 mb-4">Upravljanje fakturima i plaćanjima</p>
            <div className="flex gap-2 justify-content-center">
              <Button
                label="Prijavi se"
                icon="pi pi-sign-in"
                onClick={() => setShowRegister(false)}
                className={!showRegister ? 'p-button-outlined' : ''}
              />
              <Button
                label="Registruj se"
                icon="pi pi-user-plus"
                onClick={() => setShowRegister(true)}
                className={showRegister ? 'p-button-outlined' : ''}
              />
            </div>
          </Card>

          {showRegister ? <Register /> : <Login />}
        </div>
      </div>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
};

export default App;
