import React, { useState } from 'react';
import { AuthProvider, useAuth } from './components/AuthContext';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import { Button } from 'primereact/button';
import 'primereact/resources/themes/lara-light-indigo/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import './App.css';

const AppContent: React.FC = () => {
  const { user, loading } = useAuth();
  const [showRegister, setShowRegister] = useState(false);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-content">
          <div className="loading-spinner">
            <i className="pi pi-spin pi-spinner text-6xl text-primary"></i>
          </div>
          <h3 className="loading-text">Učitavanje...</h3>
          <p className="loading-subtitle">Molimo sačekajte</p>
        </div>
      </div>
    );
  }

  if (user) {
    return <Dashboard />;
  }

  return (
    <div className="app-container">
      <div className="auth-container">
        <div className="auth-card">
          <div className="auth-header">
            <div className="auth-logo">
              <i className="pi pi-credit-card text-6xl text-primary"></i>
            </div>
            <h1 className="auth-title">Uptiomio</h1>
            <p className="auth-subtitle">Upravljanje fakturima i plaćanjima</p>
          </div>

          <div className="auth-tabs">
            <Button
              label="Prijavi se"
              icon="pi pi-sign-in"
              className={`tab-button ${!showRegister ? 'active' : ''}`}
              onClick={() => setShowRegister(false)}
            />
            <Button
              label="Registruj se"
              icon="pi pi-user-plus"
              className={`tab-button ${showRegister ? 'active' : ''}`}
              onClick={() => setShowRegister(true)}
            />
          </div>

          <div className="auth-content">
            {showRegister ? <Register /> : <Login />}
          </div>
        </div>
      </div>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
};

export default App;
