import React, { useState } from 'react';
import { Card } from 'primereact/card';
import { InputText } from 'primereact/inputtext';
import { InputNumber } from 'primereact/inputnumber';
import { InputTextarea } from 'primereact/inputtextarea';
import { Calendar } from 'primereact/calendar';
import { Button } from 'primereact/button';
import { Message } from 'primereact/message';
import { Dropdown } from 'primereact/dropdown';
import { invoiceAPI } from '../services/api';
import './CreateInvoice.css';

const CreateInvoice: React.FC = () => {
  const [clientName, setClientName] = useState('');
  const [clientEmail, setClientEmail] = useState('');
  const [amount, setAmount] = useState<number | null>(null);
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState<Date | null>(null);
  const [paymentMethod, setPaymentMethod] = useState('');
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const paymentMethods = [
    { label: 'Payoneer', value: 'Payoneer' },
    { label: 'Western Union', value: 'Western Union' }
  ];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      await invoiceAPI.createInvoice({
        clientName,
        clientEmail,
        amount,
        description,
        dueDate,
        paymentMethod
      });

      setSuccess('Faktura je uspešno kreirana!');
      // Reset form
      setClientName('');
      setClientEmail('');
      setAmount(null);
      setDescription('');
      setDueDate(null);
      setPaymentMethod('');
    } catch (error: any) {
      setError(error.response?.data?.message || 'Greška pri kreiranju fakture');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="create-invoice-container">
      <Card className="create-invoice-card">
        <div className="card-header">
          <div className="header-icon">
            <i className="pi pi-plus-circle"></i>
          </div>
          <div className="header-text">
            <h2 className="card-title">Kreiraj novu fakturu</h2>
            <p className="card-subtitle">Dodaj novu fakturu u sistem</p>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="invoice-form">
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="clientName" className="form-label">
                <i className="pi pi-user"></i>
                Ime klijenta
              </label>
              <InputText
                id="clientName"
                value={clientName}
                onChange={(e) => setClientName(e.target.value)}
                className="form-input"
                placeholder="Unesite ime klijenta"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="clientEmail" className="form-label">
                <i className="pi pi-envelope"></i>
                Email klijenta
              </label>
              <InputText
                id="clientEmail"
                type="email"
                value={clientEmail}
                onChange={(e) => setClientEmail(e.target.value)}
                className="form-input"
                placeholder="Unesite email klijenta"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="amount" className="form-label">
                <i className="pi pi-dollar"></i>
                Iznos (RSD)
              </label>
              <InputNumber
                id="amount"
                value={amount}
                onValueChange={(e) => setAmount(e.value || null)}
                className="form-input"
                mode="currency"
                currency="RSD"
                locale="sr-RS"
                placeholder="0.00"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="dueDate" className="form-label">
                <i className="pi pi-calendar"></i>
                Datum dospijeća
              </label>
              <Calendar
                id="dueDate"
                value={dueDate}
                onChange={(e) => setDueDate(e.value as Date)}
                className="form-input"
                placeholder="Izaberite datum"
                required
                showIcon
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="description" className="form-label">
                <i className="pi pi-file-text"></i>
                Opis fakture
              </label>
              <InputTextarea
                id="description"
                value={description}
                onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => setDescription(e.target.value)}
                className="form-input"
                placeholder="Unesite opis fakture..."
                rows={4}
                required
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="paymentMethod" className="form-label">
                <i className="pi pi-credit-card"></i>
                Preporučeni način plaćanja
              </label>
              <Dropdown
                id="paymentMethod"
                value={paymentMethod}
                options={paymentMethods}
                onChange={(e) => setPaymentMethod(e.value)}
                className="form-input"
                placeholder="Izaberite način plaćanja (opciono)"
              />
            </div>
          </div>

          {error && (
            <Message severity="error" text={error} className="form-message" />
          )}

          {success && (
            <Message severity="success" text={success} className="form-message" />
          )}

          <div className="form-actions">
            <Button
              type="submit"
              label="Kreiraj fakturu"
              icon="pi pi-plus"
              className="submit-button"
              loading={loading}
              size="large"
            />
          </div>
        </form>
      </Card>
    </div>
  );
};

export default CreateInvoice;
