import React, { useState } from 'react';
import { Card } from 'primereact/card';
import { InputText } from 'primereact/inputtext';
import { Password } from 'primereact/password';
import { Button } from 'primereact/button';
import { Message } from 'primereact/message';
import { Divider } from 'primereact/divider';
import { useAuth } from './AuthContext';

const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      await login(email, password);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="login-logo">
            <i className="pi pi-credit-card text-6xl text-primary"></i>
          </div>
          <h1 className="login-title">Uptiomio</h1>
          <p className="login-subtitle">Upravljanje fakturima i plaćanjima</p>
        </div>

        <Card className="login-form-card">
          <form onSubmit={handleSubmit} className="login-form">
            <div className="field">
              <label htmlFor="email" className="field-label">Email adresa</label>
              <InputText
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="field-input"
                placeholder="Unesite vaš email"
                required
              />
            </div>

            <div className="field">
              <label htmlFor="password" className="field-label">Lozinka</label>
              <Password
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="field-input"
                placeholder="Unesite vašu lozinku"
                required
                feedback={false}
                toggleMask
              />
            </div>

            {error && (
              <Message severity="error" text={error} className="error-message" />
            )}

            <Button
              type="submit"
              label="Prijavi se"
              className="login-button"
              loading={loading}
              size="large"
            />
          </form>
        </Card>
      </div>

      <style jsx>{`
        .login-container {
          min-height: 100vh;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          display: flex;
          align-items: center;
          justify-content: center;
          padding: 20px;
        }

        .login-card {
          width: 100%;
          max-width: 420px;
          text-align: center;
        }

        .login-header {
          margin-bottom: 2rem;
        }

        .login-logo {
          margin-bottom: 1rem;
        }

        .login-title {
          font-size: 2.5rem;
          font-weight: 700;
          color: white;
          margin: 0 0 0.5rem 0;
          text-shadow: 0 2px 4px rgba(0,0,0,0.3);
        }

        .login-subtitle {
          color: rgba(255,255,255,0.9);
          font-size: 1.1rem;
          margin: 0;
        }

        .login-form-card {
          box-shadow: 0 20px 40px rgba(0,0,0,0.1);
          border-radius: 16px;
          border: none;
        }

        .login-form {
          padding: 0;
        }

        .field {
          margin-bottom: 1.5rem;
          text-align: left;
        }

        .field-label {
          display: block;
          font-weight: 600;
          color: #374151;
          margin-bottom: 0.5rem;
          font-size: 0.9rem;
        }

        .field-input {
          width: 100%;
          padding: 0.75rem;
          border: 2px solid #e5e7eb;
          border-radius: 8px;
          font-size: 1rem;
          transition: all 0.2s;
        }

        .field-input:focus {
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
        }

        .login-button {
          width: 100%;
          padding: 0.875rem;
          font-size: 1rem;
          font-weight: 600;
          border-radius: 8px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          border: none;
          margin-top: 1rem;
        }

        .error-message {
          margin-bottom: 1rem;
        }
      `}</style>
    </div>
  );
};

export default Login;
