import React, { useState, useEffect } from 'react';
import { Card } from 'primereact/card';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import { InputNumber } from 'primereact/inputnumber';
import { InputTextarea } from 'primereact/inputtextarea';
import { Message } from 'primereact/message';
import './HiltiProizvodi.css';

interface Proizvod {
  _id?: string;
  sifra: string;
  naziv: string;
  kategorija: string;
  kolicina: number;
  jedinicaMere: string;
  cena?: number;
  opis?: string;
}

const HiltiProizvodi: React.FC = () => {
  const [proizvodi, setProizvodi] = useState<Proizvod[]>([]);
  const [displayDialog, setDisplayDialog] = useState(false);
  const [proizvod, setProizvod] = useState<Proizvod>({
    sifra: '',
    naziv: '',
    kategorija: '',
    kolicina: 0,
    jedinicaMere: 'kom',
    cena: 0,
    opis: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadProizvode();
  }, []);

  const loadProizvode = async () => {
    setLoading(true);
    try {
      // TODO: Implementirati API poziv
      // Privremeni dummy data
      setProizvodi([
        { _id: '1', sifra: 'HLT-001', naziv: 'Hilti TE 60-ATC/AVR', kategorija: 'Kombinovani čekić', kolicina: 50, jedinicaMere: 'kom', cena: 85000 },
        { _id: '2', sifra: 'HLT-002', naziv: 'Hilti TE-CX SDS', kategorija: 'Burgije', kolicina: 200, jedinicaMere: 'kom', cena: 2500 },
        { _id: '3', sifra: 'HLT-003', naziv: 'Hilti X-BTM', kategorija: 'Zavrtnji', kolicina: 5000, jedinicaMere: 'kom', cena: 15 }
      ]);
    } catch (err) {
      setError('Greška pri učitavanju proizvoda');
    } finally {
      setLoading(false);
    }
  };

  const openNew = () => {
    setProizvod({
      sifra: '',
      naziv: '',
      kategorija: '',
      kolicina: 0,
      jedinicaMere: 'kom',
      cena: 0,
      opis: ''
    });
    setDisplayDialog(true);
  };

  const hideDialog = () => {
    setDisplayDialog(false);
    setError('');
  };

  const saveProizvod = async () => {
    setLoading(true);
    setError('');
    try {
      // TODO: Implementirati API poziv
      setSuccess('Proizvod uspešno sačuvan');
      hideDialog();
      loadProizvode();
    } catch (err) {
      setError('Greška pri čuvanju proizvoda');
    } finally {
      setLoading(false);
    }
  };

  const priceBodyTemplate = (rowData: Proizvod) => {
    return rowData.cena ? `${rowData.cena.toLocaleString('sr-RS')} RSD` : '-';
  };

  const quantityBodyTemplate = (rowData: Proizvod) => {
    return `${rowData.kolicina} ${rowData.jedinicaMere}`;
  };

  const dialogFooter = (
    <div>
      <Button label="Otkaži" icon="pi pi-times" onClick={hideDialog} className="p-button-text" />
      <Button label="Sačuvaj" icon="pi pi-check" onClick={saveProizvod} loading={loading} />
    </div>
  );

  return (
    <div className="hilti-proizvodi-container">
      <Card className="hilti-proizvodi-card">
        <div className="card-header">
          <div className="header-content">
            <i className="pi pi-box text-4xl text-primary"></i>
            <div>
              <h1 className="card-title">Hilti Proizvodi</h1>
              <p className="card-subtitle">Katalog proizvoda i zaliha</p>
            </div>
          </div>
          <Button 
            label="Novi proizvod" 
            icon="pi pi-plus" 
            onClick={openNew}
            className="p-button-success"
          />
        </div>

        {error && <Message severity="error" text={error} className="mb-3" />}
        {success && <Message severity="success" text={success} className="mb-3" />}

        <DataTable 
          value={proizvodi} 
          loading={loading}
          paginator 
          rows={10}
          className="mt-3"
          emptyMessage="Nema podataka o proizvodima"
          filterDisplay="row"
        >
          <Column field="sifra" header="Šifra" sortable filter />
          <Column field="naziv" header="Naziv" sortable filter />
          <Column field="kategorija" header="Kategorija" sortable filter />
          <Column field="kolicina" header="Količina" body={quantityBodyTemplate} sortable />
          <Column field="cena" header="Cena" body={priceBodyTemplate} sortable />
        </DataTable>
      </Card>

      <Dialog
        visible={displayDialog}
        style={{ width: '600px' }}
        header="Novi proizvod"
        modal
        footer={dialogFooter}
        onHide={hideDialog}
      >
        <div className="field">
          <label htmlFor="sifra">Šifra proizvoda</label>
          <InputText
            id="sifra"
            value={proizvod.sifra}
            onChange={(e) => setProizvod({ ...proizvod, sifra: e.target.value })}
            className="w-full"
            placeholder="npr. HLT-001"
          />
        </div>
        <div className="field">
          <label htmlFor="naziv">Naziv</label>
          <InputText
            id="naziv"
            value={proizvod.naziv}
            onChange={(e) => setProizvod({ ...proizvod, naziv: e.target.value })}
            className="w-full"
            placeholder="Naziv proizvoda"
          />
        </div>
        <div className="field">
          <label htmlFor="kategorija">Kategorija</label>
          <InputText
            id="kategorija"
            value={proizvod.kategorija}
            onChange={(e) => setProizvod({ ...proizvod, kategorija: e.target.value })}
            className="w-full"
            placeholder="Kategorija proizvoda"
          />
        </div>
        <div className="field">
          <label htmlFor="kolicina">Količina</label>
          <InputNumber
            id="kolicina"
            value={proizvod.kolicina}
            onValueChange={(e) => setProizvod({ ...proizvod, kolicina: e.value || 0 })}
            className="w-full"
            placeholder="Količina na stanju"
          />
        </div>
        <div className="field">
          <label htmlFor="jedinicaMere">Jedinica mere</label>
          <InputText
            id="jedinicaMere"
            value={proizvod.jedinicaMere}
            onChange={(e) => setProizvod({ ...proizvod, jedinicaMere: e.target.value })}
            className="w-full"
            placeholder="kom, kg, m, itd."
          />
        </div>
        <div className="field">
          <label htmlFor="cena">Cena (RSD)</label>
          <InputNumber
            id="cena"
            value={proizvod.cena}
            onValueChange={(e) => setProizvod({ ...proizvod, cena: e.value || 0 })}
            className="w-full"
            mode="decimal"
            minFractionDigits={2}
            placeholder="Cena"
          />
        </div>
        <div className="field">
          <label htmlFor="opis">Opis</label>
          <InputTextarea
            id="opis"
            value={proizvod.opis}
            onChange={(e) => setProizvod({ ...proizvod, opis: e.target.value })}
            className="w-full"
            rows={3}
            placeholder="Dodatni opis proizvoda"
          />
        </div>
      </Dialog>
    </div>
  );
};

export default HiltiProizvodi;
