import React, { useState } from 'react';
import { Card } from 'primereact/card';
import { InputText } from 'primereact/inputtext';
import { Password } from 'primereact/password';
import { Dropdown } from 'primereact/dropdown';
import { Button } from 'primereact/button';
import { Message } from 'primereact/message';
import { userAPI } from '../services/api';

const CreateUser: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState<'user' | 'admin'>('user');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const roleOptions = [
    { label: 'Korisnik', value: 'user' },
    { label: 'Administrator', value: 'admin' },
  ];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      await userAPI.createUser(name, email, password, role);
      setSuccess('Korisnik je uspešno kreiran.');
      setName('');
      setEmail('');
      setPassword('');
      setRole('user');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Greška pri kreiranju korisnika');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="create-user-container">
      <Card className="create-user-card">
        <h2>Kreiraj korisnika</h2>
        <form onSubmit={handleSubmit} className="create-user-form">
          <div className="field">
            <label htmlFor="name">Ime i prezime</label>
            <InputText id="name" value={name} onChange={(e) => setName(e.target.value)} required />
          </div>
          <div className="field">
            <label htmlFor="email">Email</label>
            <InputText id="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div className="field">
            <label htmlFor="password">Lozinka</label>
            <Password id="password" value={password} onChange={(e) => setPassword(e.target.value)} feedback={false} toggleMask required />
          </div>
          <div className="field">
            <label htmlFor="role">Uloga</label>
            <Dropdown id="role" value={role} options={roleOptions} onChange={(e) => setRole(e.value)} />
          </div>

          {error && <Message severity="error" text={error} />}
          {success && <Message severity="success" text={success} />}

          <Button type="submit" label="Kreiraj" loading={loading} />
        </form>
      </Card>
    </div>
  );
};

export default CreateUser;
