import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
});

// Add token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export const authAPI = {
  login: (email: string, password: string) =>
    api.post('/login', { email, password }),
  register: (name: string, email: string, password: string, role?: string) =>
    api.post('/register', { name, email, password, role }),
};

export const invoiceAPI = {
  getAllInvoices: () => api.get('/invoices'),
  getMyInvoices: () => api.get('/my-invoices'),
  createInvoice: (invoiceData: any) => api.post('/invoices', invoiceData),
  markAsPaid: (id: string, paymentMethod: string) =>
    api.patch(`/invoices/${id}/pay`, { paymentMethod }),
};

export const userAPI = {
  getAllUsers: () => api.get('/users'),
  createUser: (name: string, email: string, password: string, role: 'user' | 'admin') =>
    api.post('/users', { name, email, password, role }),
};

export default api;
