import React, { useState } from 'react';
import { Card } from 'primereact/card';
import { InputText } from 'primereact/inputtext';
import { Password } from 'primereact/password';
import { Button } from 'primereact/button';
import { Message } from 'primereact/message';
import { Dropdown } from 'primereact/dropdown';
import { useAuth } from './AuthContext';

const Register: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { register } = useAuth();

  const roleOptions = [
    { label: 'Korisnik', value: 'user' },
    { label: 'Administrator', value: 'admin' }
  ];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      await register(name, email, password, role);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex justify-content-center align-items-center min-h-screen">
      <Card title="Uptiomio - Registracija" className="w-30rem">
        <form onSubmit={handleSubmit}>
          <div className="field">
            <label htmlFor="name" className="block mb-2">Ime</label>
            <InputText
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full"
              required
            />
          </div>

          <div className="field">
            <label htmlFor="email" className="block mb-2">Email</label>
            <InputText
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full"
              required
            />
          </div>

          <div className="field">
            <label htmlFor="password" className="block mb-2">Lozinka</label>
            <Password
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full"
              required
              feedback={false}
            />
          </div>

          <div className="field">
            <label htmlFor="role" className="block mb-2">Uloga</label>
            <Dropdown
              id="role"
              value={role}
              options={roleOptions}
              onChange={(e) => setRole(e.value)}
              className="w-full"
            />
          </div>

          {error && (
            <Message severity="error" text={error} className="mb-3" />
          )}

          <Button
            type="submit"
            label="Registruj se"
            className="w-full"
            loading={loading}
          />
        </form>
      </Card>
    </div>
  );
};

export default Register;
