import React, { useState, useEffect } from 'react';
import { Card } from 'primereact/card';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import { Dropdown } from 'primereact/dropdown';
import { Message } from 'primereact/message';
import './HiltiRadnice.css';

interface Radnik {
  _id?: string;
  ime: string;
  prezime: string;
  pozicija: string;
  smena: string;
  telefon?: string;
  status: string;
}

const HiltiRadnice: React.FC = () => {
  const [radnici, setRadnici] = useState<Radnik[]>([]);
  const [displayDialog, setDisplayDialog] = useState(false);
  const [radnik, setRadnik] = useState<Radnik>({
    ime: '',
    prezime: '',
    pozicija: '',
    smena: '',
    telefon: '',
    status: 'Aktivan'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const pozicijaOptions = [
    { label: 'Operater', value: 'operater' },
    { label: 'Kontrolor kvaliteta', value: 'kontrolor' },
    { label: 'Vođa smene', value: 'voda' },
    { label: 'Tehničar', value: 'tehnicar' }
  ];

  const smenaOptions = [
    { label: 'Prva smena', value: 'prva' },
    { label: 'Druga smena', value: 'druga' },
    { label: 'Treća smena', value: 'treca' }
  ];

  const statusOptions = [
    { label: 'Aktivan', value: 'Aktivan' },
    { label: 'Na bolovanju', value: 'Bolovanje' },
    { label: 'Na odmoru', value: 'Odmor' },
    { label: 'Neaktivan', value: 'Neaktivan' }
  ];

  useEffect(() => {
    loadRadnike();
  }, []);

  const loadRadnike = async () => {
    setLoading(true);
    try {
      // TODO: Implementirati API poziv
      // Privremeni dummy data
      setRadnici([
        { _id: '1', ime: 'Marko', prezime: 'Marković', pozicija: 'operater', smena: 'prva', telefon: '0641234567', status: 'Aktivan' },
        { _id: '2', ime: 'Ana', prezime: 'Anić', pozicija: 'kontrolor', smena: 'druga', telefon: '0651234567', status: 'Aktivan' }
      ]);
    } catch (err) {
      setError('Greška pri učitavanju radnika');
    } finally {
      setLoading(false);
    }
  };

  const openNew = () => {
    setRadnik({
      ime: '',
      prezime: '',
      pozicija: '',
      smena: '',
      telefon: '',
      status: 'Aktivan'
    });
    setDisplayDialog(true);
  };

  const hideDialog = () => {
    setDisplayDialog(false);
    setError('');
  };

  const saveRadnik = async () => {
    setLoading(true);
    setError('');
    try {
      // TODO: Implementirati API poziv
      setSuccess('Radnik uspešno sačuvan');
      hideDialog();
      loadRadnike();
    } catch (err) {
      setError('Greška pri čuvanju radnika');
    } finally {
      setLoading(false);
    }
  };

  const statusBodyTemplate = (rowData: Radnik) => {
    const statusClass = rowData.status === 'Aktivan' ? 'p-badge-success' : 'p-badge-warning';
    return <span className={`p-badge ${statusClass}`}>{rowData.status}</span>;
  };

  const dialogFooter = (
    <div>
      <Button label="Otkaži" icon="pi pi-times" onClick={hideDialog} className="p-button-text" />
      <Button label="Sačuvaj" icon="pi pi-check" onClick={saveRadnik} loading={loading} />
    </div>
  );

  return (
    <div className="hilti-radnice-container">
      <Card className="hilti-radnice-card">
        <div className="card-header">
          <div className="header-content">
            <i className="pi pi-users text-4xl text-primary"></i>
            <div>
              <h1 className="card-title">Hilti Radnici</h1>
              <p className="card-subtitle">Upravljanje radnicima i radnim mestima</p>
            </div>
          </div>
          <Button 
            label="Novi radnik" 
            icon="pi pi-plus" 
            onClick={openNew}
            className="p-button-success"
          />
        </div>

        {error && <Message severity="error" text={error} className="mb-3" />}
        {success && <Message severity="success" text={success} className="mb-3" />}

        <DataTable 
          value={radnici} 
          loading={loading}
          paginator 
          rows={10}
          className="mt-3"
          emptyMessage="Nema podataka o radnicima"
        >
          <Column field="ime" header="Ime" sortable />
          <Column field="prezime" header="Prezime" sortable />
          <Column field="pozicija" header="Pozicija" sortable />
          <Column field="smena" header="Smena" sortable />
          <Column field="telefon" header="Telefon" />
          <Column field="status" header="Status" body={statusBodyTemplate} sortable />
        </DataTable>
      </Card>

      <Dialog
        visible={displayDialog}
        style={{ width: '600px' }}
        header="Novi radnik"
        modal
        footer={dialogFooter}
        onHide={hideDialog}
      >
        <div className="field">
          <label htmlFor="ime">Ime</label>
          <InputText
            id="ime"
            value={radnik.ime}
            onChange={(e) => setRadnik({ ...radnik, ime: e.target.value })}
            className="w-full"
            placeholder="Ime radnika"
          />
        </div>
        <div className="field">
          <label htmlFor="prezime">Prezime</label>
          <InputText
            id="prezime"
            value={radnik.prezime}
            onChange={(e) => setRadnik({ ...radnik, prezime: e.target.value })}
            className="w-full"
            placeholder="Prezime radnika"
          />
        </div>
        <div className="field">
          <label htmlFor="pozicija">Pozicija</label>
          <Dropdown
            id="pozicija"
            value={radnik.pozicija}
            options={pozicijaOptions}
            onChange={(e) => setRadnik({ ...radnik, pozicija: e.value })}
            className="w-full"
            placeholder="Izaberite poziciju"
          />
        </div>
        <div className="field">
          <label htmlFor="smena">Smena</label>
          <Dropdown
            id="smena"
            value={radnik.smena}
            options={smenaOptions}
            onChange={(e) => setRadnik({ ...radnik, smena: e.value })}
            className="w-full"
            placeholder="Izaberite smenu"
          />
        </div>
        <div className="field">
          <label htmlFor="telefon">Telefon</label>
          <InputText
            id="telefon"
            value={radnik.telefon}
            onChange={(e) => setRadnik({ ...radnik, telefon: e.target.value })}
            className="w-full"
            placeholder="Broj telefona"
          />
        </div>
        <div className="field">
          <label htmlFor="status">Status</label>
          <Dropdown
            id="status"
            value={radnik.status}
            options={statusOptions}
            onChange={(e) => setRadnik({ ...radnik, status: e.value })}
            className="w-full"
            placeholder="Izaberite status"
          />
        </div>
      </Dialog>
    </div>
  );
};

export default HiltiRadnice;
