import React, { useState, useEffect } from 'react';
import { Card } from 'primereact/card';
import { Calendar } from 'primereact/calendar';
import { Button } from 'primereact/button';
import { Chart } from 'primereact/chart';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Message } from 'primereact/message';
import './HiltiIzvestaji.css';

interface IzvestajData {
  datum: string;
  proizvodnja: number;
  radnici: number;
  produktivnost: number;
}

const HiltiIzvestaji: React.FC = () => {
  const [startDate, setStartDate] = useState<Date>(new Date(new Date().setDate(new Date().getDate() - 30)));
  const [endDate, setEndDate] = useState<Date>(new Date());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [chartData, setChartData] = useState<any>(null);
  const [tableData, setTableData] = useState<IzvestajData[]>([]);

  useEffect(() => {
    loadIzvestaje();
  }, []);

  const loadIzvestaje = async () => {
    setLoading(true);
    try {
      // TODO: Implementirati API poziv
      // Privremeni dummy data
      const dummyData: IzvestajData[] = [
        { datum: '2025-11-01', proizvodnja: 150, radnici: 12, produktivnost: 12.5 },
        { datum: '2025-11-02', proizvodnja: 165, radnici: 12, produktivnost: 13.75 },
        { datum: '2025-11-03', proizvodnja: 142, radnici: 11, produktivnost: 12.9 },
        { datum: '2025-11-04', proizvodnja: 178, radnici: 13, produktivnost: 13.7 },
        { datum: '2025-11-05', proizvodnja: 155, radnici: 12, produktivnost: 12.9 }
      ];

      setTableData(dummyData);

      const chart = {
        labels: dummyData.map(d => d.datum),
        datasets: [
          {
            label: 'Proizvodnja',
            data: dummyData.map(d => d.proizvodnja),
            backgroundColor: '#3b82f6',
            borderColor: '#2563eb',
            borderWidth: 2,
            tension: 0.4
          },
          {
            label: 'Produktivnost',
            data: dummyData.map(d => d.produktivnost),
            backgroundColor: '#10b981',
            borderColor: '#059669',
            borderWidth: 2,
            tension: 0.4
          }
        ]
      };

      setChartData(chart);
    } catch (err) {
      setError('Greška pri učitavanju izveštaja');
    } finally {
      setLoading(false);
    }
  };

  const exportToPDF = () => {
    // TODO: Implementirati export u PDF
    alert('Export u PDF - funkcionalnost će biti implementirana');
  };

  const exportToExcel = () => {
    // TODO: Implementirati export u Excel
    alert('Export u Excel - funkcionalnost će biti implementirana');
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Pregled proizvodnje i produktivnosti'
      }
    },
    scales: {
      y: {
        beginAtZero: true
      }
    }
  };

  const calculateStats = () => {
    const totalProizvodnja = tableData.reduce((sum, item) => sum + item.proizvodnja, 0);
    const avgRadnici = tableData.length > 0 
      ? Math.round(tableData.reduce((sum, item) => sum + item.radnici, 0) / tableData.length) 
      : 0;
    const avgProduktivnost = tableData.length > 0
      ? (tableData.reduce((sum, item) => sum + item.produktivnost, 0) / tableData.length).toFixed(2)
      : 0;

    return { totalProizvodnja, avgRadnici, avgProduktivnost };
  };

  const stats = calculateStats();

  return (
    <div className="hilti-izvestaji-container">
      <Card className="hilti-izvestaji-card">
        <div className="card-header">
          <div className="header-content">
            <i className="pi pi-chart-bar text-4xl text-primary"></i>
            <div>
              <h1 className="card-title">Hilti Izveštaji</h1>
              <p className="card-subtitle">Analitika i statistika proizvodnje</p>
            </div>
          </div>
          <div className="export-buttons">
            <Button 
              label="PDF" 
              icon="pi pi-file-pdf" 
              onClick={exportToPDF}
              className="p-button-danger"
            />
            <Button 
              label="Excel" 
              icon="pi pi-file-excel" 
              onClick={exportToExcel}
              className="p-button-success"
            />
          </div>
        </div>

        {error && <Message severity="error" text={error} className="mb-3" />}

        <div className="date-filter-section">
          <div className="date-filter">
            <label>Od datuma:</label>
            <Calendar 
              value={startDate} 
              onChange={(e) => setStartDate(e.value as Date)} 
              dateFormat="dd.mm.yy"
              showIcon
            />
          </div>
          <div className="date-filter">
            <label>Do datuma:</label>
            <Calendar 
              value={endDate} 
              onChange={(e) => setEndDate(e.value as Date)} 
              dateFormat="dd.mm.yy"
              showIcon
            />
          </div>
          <Button 
            label="Prikaži" 
            icon="pi pi-search" 
            onClick={loadIzvestaje}
            loading={loading}
          />
        </div>

        <div className="stats-cards">
          <Card className="stat-card">
            <div className="stat-content">
              <i className="pi pi-box stat-icon"></i>
              <div>
                <h3>Ukupna proizvodnja</h3>
                <p className="stat-value">{stats.totalProizvodnja}</p>
              </div>
            </div>
          </Card>
          <Card className="stat-card">
            <div className="stat-content">
              <i className="pi pi-users stat-icon"></i>
              <div>
                <h3>Prosečan broj radnika</h3>
                <p className="stat-value">{stats.avgRadnici}</p>
              </div>
            </div>
          </Card>
          <Card className="stat-card">
            <div className="stat-content">
              <i className="pi pi-chart-line stat-icon"></i>
              <div>
                <h3>Prosečna produktivnost</h3>
                <p className="stat-value">{stats.avgProduktivnost}</p>
              </div>
            </div>
          </Card>
        </div>

        {chartData && (
          <div className="chart-container">
            <Chart type="line" data={chartData} options={chartOptions} />
          </div>
        )}

        <div className="table-section">
          <h2>Detaljni podaci</h2>
          <DataTable 
            value={tableData} 
            loading={loading}
            paginator 
            rows={10}
            emptyMessage="Nema podataka za izabrani period"
          >
            <Column field="datum" header="Datum" sortable />
            <Column field="proizvodnja" header="Proizvodnja" sortable />
            <Column field="radnici" header="Broj radnika" sortable />
            <Column field="produktivnost" header="Produktivnost" sortable />
          </DataTable>
        </div>
      </Card>
    </div>
  );
};

export default HiltiIzvestaji;
